// Extract weather data

const PPFD_CLEAR_SKY_NOON = 1000;
const RAIN_EFFECT_FACTOR = 0.2;
const HUMIDITY_EFFECT_FACTOR = 0.1;
const MAX_CLOUD_EFFECT_FACTOR = 0.69; // Maximum effect of clouds (0.8 means 80% PPFD remains)
const CLOUDINESS_ADJUSTMENT_FACTOR = 0.5; // 50% weight on cloudiness

let weatherData = msg.payload;
var ft = flow.get('count');
var flowtime = ft.results;
let ppfdData = []
let ti = []
let save = []

for (let i = 0; i < weatherData.length; i++) 
{
    let entry = weatherData[i];
    const cloudiness = entry.cloud;  

    let rain_effect = 0;

    if (entry.rain !== undefined) {
        const rain_mm = entry.rain;
        rain_effect = Math.min(rain_mm * RAIN_EFFECT_FACTOR, 1); // Convert mm of rain to a reduction factor (capped at 100%)
    } else {
        const humidity = entry.humidity;
        rain_effect = Math.min(humidity * HUMIDITY_EFFECT_FACTOR / 100, 1); // Convert % humidity to a reduction factor (capped at 100%)
    }

    const sunrise = new Date(flowtime.sunrise);
    const sunset = new Date(flowtime.sunset);
    const current_time = new Date(entry.time);

    const today = new Date();
    const ctOnlyTime = new Date(today.getFullYear(), today.getMonth(), today.getDate(),
        current_time.getHours(), current_time.getMinutes(), current_time.getSeconds());
    const srOnlyTime = new Date(today.getFullYear(), today.getMonth(), today.getDate(),
        sunrise.getHours(), sunrise.getMinutes(), sunrise.getSeconds());
    const ssOnlyTime = new Date(today.getFullYear(), today.getMonth(), today.getDate(),
        sunset.getHours(), sunset.getMinutes(), sunset.getSeconds());

    if (ctOnlyTime.getTime() < srOnlyTime.getTime() || ctOnlyTime.getTime() > ssOnlyTime.getTime()) {
        ppfdData.push({ x: current_time.getTime(), y: 0.0 });
        save.push({
            time: current_time,
            daytime: "false"
        })
    }
    else {
        const noon_time = new Date((srOnlyTime.getTime() + ssOnlyTime.getTime()) / 2);
        const time_difference = Math.abs(ctOnlyTime.getTime() - noon_time.getTime()) / 1000; // Difference in seconds
        const time_factor = 1 - (time_difference / ((ssOnlyTime.getTime() - srOnlyTime.getTime()) / 2));
        const ppfd_clear_sky = PPFD_CLEAR_SKY_NOON * Math.max(0.1, time_factor); // Limit time factor

        const non_linear_cloudiness_effect = (1 - Math.pow((cloudiness / 100), CLOUDINESS_ADJUSTMENT_FACTOR));
        const adjusted_cloudiness_effect = 1 - ((1 - non_linear_cloudiness_effect) * (1 - MAX_CLOUD_EFFECT_FACTOR));

        const ppfd_adjusted = ppfd_clear_sky * adjusted_cloudiness_effect * (1 - rain_effect);

        ppfdData.push({ x: current_time.getTime(), y: ppfd_adjusted });
        save.push({
            time: current_time,
            daytime: "true"
        })
    }

    ti.push(entry.time);
}

msg.payload = [
    {
        series: ["PPFD"],
        data: [ppfdData],
        labels: ["PPFD"],
        ti: [ti],
        sv: [save]
    }
];

return msg;

